import React from "react";
import { Link } from "react-router-dom";
import "assets/css/style.scss";
import "assets/css/user.scss";
import PV from "password-validator";
import * as EmailValidator from "email-validator";
import KakaoLogin from "components/user/snsLogin/Kakao";
import GoogleLogin from "components/user/snsLogin/Google";
import UserApi from "apis/UserApi";

class Login extends React.Component {
  state = {
    email: "",
    password: "",
    passwordSchema: new PV(),
    error: {
      email: false,
      password: false
    },
    isSubmit: false,
    component: this
  };
  componentDidMount() {
    console.log(window.location.href);
    this.state.passwordSchema
      .is()
      .min(8)
      .is()
      .max(100)
      .has()
      .digits()
      .has()
      .letters();
  }
  checkForm = () => {
    let error = { ...this.state.error };
    if (
      this.state.email.length >= 0 &&
      !EmailValidator.validate(this.state.email)
    ) {
      error.email = "이메일 형식이 아닙니다.";
    } else {
      error.email = false;
    }
    if (
      this.state.password.length >= 0 &&
      !this.state.passwordSchema.validate(this.state.password)
    )
      error.password = "영문,숫자 포함 8 자리이상이어야 합니다.";
    else error.password = false;
    this.setState({ error: error });
    let isSubmit = true;
    Object.values(this.state.error).map(v => {
      if (v) isSubmit = false;
      return v;
    });
    this.setState({
      isSubmit: isSubmit
    });
    console.log("isSubmit: ", this.state.isSubmit);
  };
  login() {
    if (this.state.isSubmit) {
      let { email, password } = this.state;
      let data = {
        email,
        password
      };
      //요청 후에는 버튼 비활성화
      this.setState({ isSubmit: false });
      // this.state.isSubmit = false;

      UserApi.requestLogin(
        data,
        res => {
          //통신을 통해 전달받은 값 콘솔에 출력

          //정상적인 응답이 와도 우선 로그인 실패로 간주하고 알람 띄우기
          alert("로그인 실패하였습니다.");

          //요청이 끝나면 버튼 활성화
          this.setState({ isSubmit: true });
          // this.state.isSubmit = true;
        },
        error => {
          //요청이 끝나면 버튼 활성화
          this.setState({ isSubmit: true });
          //this.state.isSubmit = true;
        }
      );
    }
  }
  handleInput = e => {
    console.log(e.target.value);
    this.setState({ email: e.target.value });
    this.checkForm();
  };
  handleInput2 = e => {
    this.setState({ password: e.target.value });
    this.checkForm();
  };
  render() {
    return (
      <div className="user" id="login">
        <div className="wrapC">
          <h1 className="title">로그인</h1>
          <div className="input-with-label">
            <input
              value={this.state.email}
              onChange={this.handleInput}
              id="email"
              placeholder="이메일을 입력하세요."
              type="text"
            />
            <label htmlFor="email">이메일</label>
            <div className="error-text" v-if="error.email">
              {this.state.error.email}
            </div>
          </div>

          <div className="input-with-label">
            <input
              value={this.state.password}
              type="password"
              id="password"
              onKeyDown={event => {
                console.log(event.key);
                if (event.key === "Enter") {
                  this.login();
                }
              }}
              onChange={this.handleInput2}
              placeholder="비밀번호를 입력하세요."
            />
            <label htmlFor="password">비밀번호</label>
            <div className="error-text" v-if="error.password">
              {this.state.error.password}
            </div>
          </div>
          <button
            disabled={!this.state.isSubmit}
            className="btn btn--back btn--login"
            onClick={() => this.login()}
          >
            로그인
          </button>

          <div className="sns-login">
            <div className="text">
              <p>SNS 간편 로그인</p>
              <div className="bar"></div>
            </div>

            <KakaoLogin />
            <GoogleLogin />
          </div>
          <div className="add-option">
            <div className="bar"></div>

            <Link to={"/findpassword"} className="btn--back">
              <button className="btn btn--back btn--password">
                비밀번호 찾기
              </button>
            </Link>

            <Link to={"/join"} className="btn--back">
              <button className="btn btn--back btn--join">회원가입</button>
            </Link>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
