import React from "react";
import { Link } from "react-router-dom";
import "assets/css/style.scss";
import "assets/css/user.scss";
import PV from "password-validator";
import * as EmailValidator from "email-validator";
import KakaoLogin from "components/user/snsLogin/Kakao";
import GoogleLogin from "components/user/snsLogin/Google";
import UserApi from "apis/UserApi";

class Login extends React.Component {
  constructor({ match }) {}
  state = {
    email: "",
    password: "",
    passwordSchema: new PV(),
    error: {
      email: false,
      password: false
    },
    isSubmit: false,
    component: this
  };
  componentDidMount() {
    this.passwordSchema
      .is()
      .min(8)
      .is()
      .max(100)
      .has()
      .digits()
      .has()
      .letters();
  }
  checkForm() {
    if (this.email.length >= 0 && !EmailValidator.validate(this.email))
      this.error.email = "이메일 형식이 아닙니다.";
    else this.error.email = false;

    if (
      this.password.length >= 0 &&
      !this.passwordSchema.validate(this.password)
    )
      this.error.password = "영문,숫자 포함 8 자리이상이어야 합니다.";
    else this.error.password = false;

    let isSubmit = true;
    Object.values(this.error).map(v => {
      if (v) isSubmit = false;
    });
    this.isSubmit = isSubmit;
  }
  login() {
    if (this.isSubmit) {
      let { email, password } = this;
      let data = {
        email,
        password
      };

      //요청 후에는 버튼 비활성화
      this.isSubmit = false;

      UserApi.requestLogin(
        data,
        res => {
          //통신을 통해 전달받은 값 콘솔에 출력
          //                        console.log(res);

          //요청이 끝나면 버튼 활성화
          this.isSubmit = true;
        },
        error => {
          //요청이 끝나면 버튼 활성화
          this.isSubmit = true;
        }
      );
    }
  }
  handleInput() {}
  render() {
    return (
      <div class="user" id="login">
        <div class="wrapC">
          <div class="input-with-label">
            <input
              v-model="email"
              v-bind="{error : error.password, complete:!error.password&&password.length!==0}"
              onKeyDown={event => {
                if (event.key === "Enter") {
                  this.login();
                }
              }}
              id="email"
              placeholder="이메일을 입력하세요."
              type="text"
            />
            <label for="email">이메일</label>
            <div class="error-text" v-if="error.email">
              {error.email}
            </div>
          </div>

          <div class="input-with-label">
            <input
              v-model="password"
              type="password"
              v-bind="{error : error.password, complete:!error.password&&password.length!==0}"
              id="password"
              onKeyDown={event => {
                if (event.key === "Enter") {
                  this.login();
                }
              }}
              placeholder="비밀번호를 입력하세요."
            />
            <label for="password">비밀번호</label>
            <div class="error-text" v-if="error.password">
              {error.password}
            </div>
          </div>
          {/* <button class="btn btn--back btn--login" v-on:click="login" :disabled="!isSubmit"
                    :class="{disabled : !isSubmit}">
                로그인

            </button> */}

          <div class="sns-login">
            <div class="text">
              <p>SNS 간편 로그인</p>
              <div class="bar"></div>
            </div>

            <KakaoLogin />
            <GoogleLogin />
          </div>
          <div class="add-option">
            <div class="text">
              <p>혹시</p>
              <div class="bar"></div>
            </div>
            <div class="wrap">
              <p>비밀번호를 잊으셨나요?</p>
              <Link to="{'????'}" class="btn--text">
                비밀번호 찾기
              </Link>
            </div>
            <div class="wrap">
              <p>아직 회원이 아니신가요?</p>
              <Link to="{name:'Join'}" class="btn--text">
                가입하기
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
