import React from "react";
import { Link } from "react-router-dom";
import "assets/css/style.scss";
import "assets/css/user.scss";
import PV from "password-validator";
import * as EmailValidator from "email-validator";
import KakaoLogin from "components/user/snsLogin/Kakao";
import GoogleLogin from "components/user/snsLogin/Google";
import UserApi from "apis/UserApi";

class Login extends React.Component {
  state = {
    email: "",
    password: "",
    passwordSchema: new PV(),
    error: {
      email: false,
      password: false
    },
    isSubmit: false,
    component: this
  };
  componentDidMount() {
    this.state.passwordSchema
      .is()
      .min(8)
      .is()
      .max(100)
      .has()
      .digits()
      .has()
      .letters();
  }
  checkForm = () => {
    if (
      this.state.email.length >= 0 &&
      !EmailValidator.validate(this.state.email)
    ) {
      console.log("이메일 형식이 아님");
      this.setState(prevState => {
        let error = Object.assign({}, prevState.error);
        error.email = "이메일 형식이 아닙니다.";
        return { error };
      });
    } else {
      console.log("이메일 형식임");
      this.setState(prevState => ({
        error: { ...prevState.error, email: false }
      }));
    }
    console.log(this.state.error);
    if (
      this.state.password.length >= 0 &&
      !this.state.passwordSchema.validate(this.state.password)
    )
      this.setState({
        error: {
          ...this.state.error,
          password: "영문,숫자 포함 8 자리이상이어야 합니다."
        }
      });
    this.setState({
      error: {
        ...this.state.error,
        password: false
      }
    });

    let isSubmit = true;
    Object.values(this.state.error).map(v => {
      if (v) isSubmit = false;
    });
    this.setState({
      isSubmit: isSubmit
    });
  };
  login() {
    if (this.isSubmit) {
      let { email, password } = this;
      let data = {
        email,
        password
      };

      //요청 후에는 버튼 비활성화
      this.isSubmit = false;

      UserApi.requestLogin(
        data,
        res => {
          //통신을 통해 전달받은 값 콘솔에 출력
          //                        console.log(res);

          //요청이 끝나면 버튼 활성화
          this.isSubmit = true;
        },
        error => {
          //요청이 끝나면 버튼 활성화
          this.isSubmit = true;
        }
      );
    }
  }
  handleInput = e => {
    console.log("e: ", e.target.value);
    this.setState({ email: e.target.value });
    this.checkForm();
  };
  render() {
    return (
      <div className="user" id="login">
        <div className="wrapC">
          <div className="input-with-label">
            <input
              value={this.state.email}
              v-bind="{error : error.password, complete:!error.password&&password.length!==0}"
              onKeyDown={event => {
                if (event.key === "Enter") {
                  this.login();
                }
              }}
              onChange={this.handleInput}
              id="email"
              placeholder="이메일을 입력하세요."
              type="text"
            />
            <label htmlFor="email">이메일</label>
            <div className="error-text" v-if="error.email">
              {this.state.error.email}
            </div>
          </div>

          <div className="input-with-label">
            <input
              v-model="password"
              type="password"
              v-bind="{error : error.password, complete:!error.password&&password.length!==0}"
              id="password"
              onKeyDown={event => {
                if (event.key === "Enter") {
                  this.login();
                }
              }}
              placeholder="비밀번호를 입력하세요."
            />
            <label htmlFor="password">비밀번호</label>
            <div className="error-text" v-if="error.password">
              {this.state.error.password}
            </div>
          </div>
          <button
            className="btn btn--back btn--login"
            onClick={this.login}
            disabled={!this.isSubmit}
          >
            {/* :className="{disabled : !isSubmit}"> */}
            로그인
          </button>

          <div className="sns-login">
            <div className="text">
              <p>SNS 간편 로그인</p>
              <div className="bar"></div>
            </div>

            <KakaoLogin />
            <GoogleLogin />
          </div>
          <div className="add-option">
            <div className="text">
              <p>혹시</p>
              <div className="bar"></div>
            </div>
            <div className="wrap">
              <p>비밀번호를 잊으셨나요?</p>
              <Link to="{'????'}" className="btn--text">
                비밀번호 찾기
              </Link>
            </div>
            <div className="wrap">
              <p>아직 회원이 아니신가요?</p>
              <Link to={`${this.props.match.url}/Join`} className="btn--text">
                가입하기
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
